figure.artwork:
KX509Request ::= SEQUENCE {
        ap-req  OCTET STRING,
        pk-hash OCTET STRING,
        pk-key  OCTET STRING
}

KX509Response ::= SEQUENCE {
        error-code[0]  INTEGER DEFAULT 0,
        hash[1]        OCTET STRING OPTIONAL,
        certificate[2] OCTET STRING OPTIONAL,
        e-text[3]      VisibleString OPTIONAL
}

