QoS-Profile-Template ::= < AVP Header: XXX >
                         { Vendor-Id }
                         { QoS-Profile-Id }
                       * [ AVP ]

QoS-Capability ::= < AVP Header: XXX >
                 1*{ QoS-Profile-Template }
                 * [ AVP ]

Classifier = {
    Classifier-Id = "web_svr_example";
    Protocol = TCP;
    Direction = OUT;
    From-Spec = {
        IP-Address-Mask = {
            IP-Address = 192.0.2.0;
            IP-Bit-Mask-Width = 24;
        }
    }
    To-Spec = {
        IP-Address = 192.0.2.123;
        IP-Address = 192.0.2.124;
        IP-Address = 192.0.2.125;
        Port = 80;
        Port = 8080;
        Port = 443;
    }
}

Classifier = {
    Classifier-Id = "web_svr_example";
    Protocol = UDP;
    Direction = OUT;
    From-Spec = {
        MAC-Address = 01:23:45:67:89:ab;
    }
    To-Spec = {
        IP-Address-Range = {
            IP-Address-Start = 192.0.2.90;
            IP-Address-End = 192.0.2.190;
        }
        Port = 5060;
        Port = 3478;
        Port-Range = {
            Port-Start = 16348;
            Port-End = 32768;
        }
    }
}
