figure.artwork:
module example-ospf {
    namespace "http://example.org/netconf/ospf";
    prefix ospf;

    import network-types {  // Access another module's def'ns
        prefix nett;
    }

    container ospf {   // Declare the top-level tag
        list area {    // Declare a list of "area" nodes
            key name;  // The key "name" identifies list members
            leaf name {
                type nett:area-id;
            }
            list interface {
                key name;
                leaf name {
                    type nett:interface-name;
                }
                leaf priority {
                    description "Designated router priority";
                    type uint8;  // The type is a constraint on
                                 // valid values for "priority".
                }
                leaf metric {
                    type uint16 {
                        range 1..65535;
                    }
                }
                leaf dead-interval {
                    units seconds;
                    type uint16 {
                        range 1..65535;
                    }
                }
            }
        }
    }
}

type union {
    type int32;
    type enumeration {
        enum "unbounded";
    }
}

module vendorx-ospf {
    namespace "http://vendorx.example.com/ospf";
    prefix vendorx;

    import example-ospf {
        prefix ospf;
    }

    augment /ospf:ospf/ospf:area/ospf:interfaces {
        leaf no-neighbor-down-notification {
            type empty;
            description "Don't inform other protocols about"
                      + " neighbor down events";
        }
    }
}

list example-bgpvpn {
    key name;
    leaf name { ... }
    leaf protocol {
        type enumeration {
            enum bgpvpn;
            enum l2vpn;
        }
    }
    leaf topology {
        type enumeration {
            enum hub-n-spoke;
            enum mesh;
        }
    }
    list members {
        key "device interface";
        leaf device { ... }
        leaf interface { ... }
    }
    list classifiers {
        ...
    }
}
